import React, { useState } from 'react';

function Catergories() {
  const [activeIndex, setActiveIndex] = useState(1);
  const onClickCategories = (index) => {
    setActiveIndex(index);
  };
  return (
    <div class="categories">
      <ul>
        <li class={activeIndex === 0 ? 'active' : ''} onClick={() => onClickCategories(0)}>
          Все
        </li>
        <li class={activeIndex === 1 ? 'active' : ''} onClick={() => onClickCategories(1)}>
          Мясные
        </li>
        <li class={activeIndex === 2 ? 'active' : ''} onClick={() => onClickCategories(2)}>
          Вегетарианская
        </li>
        <li class={activeIndex === 3 ? 'active' : ''} onClick={() => onClickCategories(3)}>
          Гриль
        </li>
        <li class={activeIndex === 4 ? 'active' : ''}>Острые</li>
        <li class={activeIndex === 5 ? 'active' : ''}>Закрытые</li>
      </ul>
    </div>
  );
}
export default Catergories;
