import React, { useEffect, useState } from 'react';
import Catergories from '../components/Categories';
import Sort from '../components/Sort';
import PizzaBlock from '../components/PizzaBlock';
import Skeleton from '../components/PizzaBlockSkeleton';
import ReactPaginate from 'react-paginate';
import Pagination from '../components/pagination/index';
import { SearchContext } from '../App';
import { useSelector, useDispatch } from 'react-redux';
import axios from 'axios';
import qs from 'qs';
import { useNavigate } from 'react-router-dom';

const Home = () => {
  const navigate = useNavigate();
  const [items, setItems] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const activeCategory = useSelector((state) => state.filter.category);
  const activeSort = useSelector((state) => state.filter.sort);
  const [currentPage, setCurrentPage] = useState(0);
  const { searchValue } = React.useContext(SearchContext);
  const sortTypes = ['rating', 'price', 'title'];

  React.useEffect(() => {
    if (window.location.search) {
      const params = qs.parse(window.location.search.substring(1));
    }
  }, []);

  useEffect(() => {
    setIsLoading(true);
    axios
      .get(
        `https://64b7eace21b9aa6eb079470b.mockapi.io/pizzas?page=${currentPage + 1}&limit=4${
          activeCategory > 0 ? `&category=${activeCategory}` : ``
        }&sortBy=${sortTypes[activeSort]}&order=desc` +
          `${searchValue ? `&search=${searchValue}` : ``}`,
      )
      .then((res) => {
        setItems(res.data);
        setIsLoading(false);
      });
    window.scrollTo(0, 0);
  }, [activeCategory, activeSort, searchValue, currentPage]);

  React.useEffect(() => {
    const queryString = qs.stringify({
      sortProperty: activeSort,
      categoryId: activeCategory,
      currentPage: currentPage,
    });

    navigate(`?${queryString}`);
  }, [activeCategory, activeSort, searchValue, currentPage]);
  const pizzas = items.map((obj) => (
    <PizzaBlock
      key={obj.id}
      title={obj.title}
      price={obj.price}
      img={obj.imageUrl}
      sizes={obj.sizes}
      types={obj.types}
    />
  ));
  const skeletons = [...new Array(8)].map(() => <Skeleton />);
  return (
    <>
      <div class="container">
        <div class="content__top">
          <Catergories />
          <Sort />
        </div>
        <h2 class="content__title">Все пиццы</h2>
        <div class="content__items">{isLoading ? skeletons : pizzas}</div>
        <Pagination currentPage={currentPage} setCurrentPage={setCurrentPage} />
      </div>
    </>
  );
};

export default Home;
