import React, { useEffect, useState } from 'react';
import Catergories from '../components/Categories';
import Sort from '../components/Sort';
import PizzaBlock from '../components/PizzaBlock';
import Skeleton from '../components/PizzaBlockSkeleton';

const Home = ({ searchValue }) => {
  const [items, setItems] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [activeSort, setActiveSort] = useState(0);
  const [activeCategoryIndex, setActiveCategoryIndex] = useState(0);

  const sortTypes = ['rating', 'price', 'title'];

  useEffect(() => {
    setIsLoading(true);
    fetch(
      `https://64b7eace21b9aa6eb079470b.mockapi.io/pizzas?${
        activeCategoryIndex > 0 ? `category=${activeCategoryIndex}` : ``
      }&sortBy=${sortTypes[activeSort]}&order=desc` +
        activeCategoryIndex +
        `${searchValue ? `&search=${searchValue}` : ``}`,
    )
      .then((res) => {
        return res.json();
      })
      .then((json) => {
        setItems(json);
        setIsLoading(false);
      });
    window.scrollTo(0, 0);
  }, [activeCategoryIndex, activeSort, searchValue]);
  const pizzas = items.map((obj) => (
    <PizzaBlock
      key={obj.id}
      title={obj.title}
      price={obj.price}
      img={obj.imageUrl}
      sizes={obj.sizes}
      types={obj.types}
    />
  ));
  const skeletons = [...new Array(8)].map(() => <Skeleton />);
  return (
    <>
      <div class="container">
        <div class="content__top">
          <Catergories
            value={activeCategoryIndex}
            onClickCategories={(id) => setActiveCategoryIndex(id)}
          />
          <Sort value={activeSort} onChangeSort={(id) => setActiveSort(id)} />
        </div>
        <h2 class="content__title">Все пиццы</h2>
        <div class="content__items">{isLoading ? skeletons : pizzas}</div>
      </div>
    </>
  );
};

export default Home;
