import React, { useEffect, useState } from 'react';
import Catergories from '../components/Categories';
import Sort from '../components/Sort';
import PizzaBlock from '../components/PizzaBlock';
import Skeleton from '../components/PizzaBlockSkeleton';
import Pagination from '../components/pagination/index';
import { SearchContext } from '../App';
import { useSelector, useDispatch } from 'react-redux';
import axios from 'axios';
import { setItems } from '../redux/slices/pizzasSlice';

const Home = async () => {
  const [isLoading, setIsLoading] = useState(true);
  const dispatch = useDispatch();
  const activeCategory = useSelector((state) => state.filter.category);
  const activeSort = useSelector((state) => state.filter.sort);
  const currentPage = useSelector((state) => state.filter.currentPage);
  //const items = useSelector((state) => state.pizzas.items);
  const items = [];
  const { searchValue } = React.useContext(SearchContext);
  const sortTypes = ['rating', 'price', 'title'];

  useEffect(() => {
    setIsLoading(true);
    axios
      .get(
        `https://64b7eace21b9aa6eb079470b.mockapi.io/pizzas?page=${currentPage + 1}&limit=4${
          activeCategory > 0 ? `&category=${activeCategory}` : ``
        }&sortBy=${sortTypes[activeSort]}&order=desc` +
          `${searchValue ? `&search=${searchValue}` : ``}`,
      )
      .then((res) => {
        setIsLoading(false);
      })
      .catch((err) => {
        console.log(err);
        setIsLoading(false);
      });
    window.scrollTo(0, 0);
  }, [activeCategory, activeSort, searchValue, currentPage]);
  /*const pizzas = items.map((obj) => (
    <PizzaBlock
      id={obj.id}
      title={obj.title}
      price={obj.price}
      img={obj.imageUrl}
      sizes={obj.sizes}
      types={obj.types}
    />
  ));*/
  const skeletons = [...new Array(8)].map(() => <Skeleton />);
  return (
    <>
      <div class="container">
        <div class="content__top">
          <Catergories />
          <Sort />
        </div>
        <h2 class="content__title">Все пиццы</h2>
        <div class="content__items">{isLoading ? skeletons : pizzas}</div>
        <Pagination />
      </div>
    </>
  );
};

export default Home;
