import React, { useState } from 'react';
import './App.css';
import './scss/app.scss';
import Header from './components/Header';
import Catergories from './components/Categories';
import Sort from './components/Sort';
import PizzaBlock from './components/PizzaBlock';

function App() {
  const [items, setItems] = useState([]);
  fetch('https://64b7eace21b9aa6eb079470b.mockapi.io/pizzas')
    .then((res) => {
      return res.json();
    })
    .then((json) => {
      setItems(json);
    });

  return (
    <div class="wrapper">
      <Header />
      <div class="content">
        <div class="container">
          <div class="content__top">
            <Catergories />
            <Sort />
          </div>
          <h2 class="content__title">Все пиццы</h2>
          <div class="content__items">
            {items.map((obj) => (
              <PizzaBlock
                key={obj.id}
                title={obj.title}
                price={obj.price}
                img={obj.imageUrl}
                sizes={obj.sizes}
                types={obj.types}
              />
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
